import React,{useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import './card.css'

function Card(props)

{
    const [seconds, setSeconds] = useState(30 * 60 * 60); // 30 hours in seconds

    useEffect(() => {
      const timer = setInterval(() => {
        setSeconds((prevSeconds) => prevSeconds - 1);
      }, 1000);
  
      return () => clearInterval(timer);
    }, []);
  
    // Convert seconds to hours, minutes, and seconds
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const remainingSeconds = seconds % 60;
    return(
        <div  className='card'>
            <div className='card-image'>
                 <img src={props.image} alt="event-img" />
            </div>
            <div className='card-event'>
                 <h3>{props.event}</h3>
            </div>
            <div className='card-description'>
                <p>{props.description}</p>
            </div>
            <hr/>
           <div className="venue-date">
            <p id="venue">Venue : {props.venue}</p>
            <p id="date">Date:{props.date}</p>
            </div>
            <div className="btn-apply">
            <Link to="/" className="btn btn-dark">APPLY</Link>
            </div>
            <div className="timer">
        <p>Time Remaining: {hours} hours, {minutes} minutes, {remainingSeconds} seconds</p>
      </div>
        </div>
    )
}
export default Card
